import React from 'react';

const Hero = () => {
  const handleDownloadPDF = () => {
    // Create a simple PDF download functionality
    const link = document.createElement('a');
    link.href = '/portfolio.pdf'; // You'll need to add this file to public folder
    link.download = 'Senior_Product_Manager_Portfolio.pdf';
    link.click();
  };

  const handleLinkedInConnect = () => {
    window.open('https://www.linkedin.com/in/rakeshsharma364/', '_blank');
  };

  return (
    <section className="min-h-screen bg-gradient-to-br from-primary-50 via-white to-primary-100 flex items-center justify-center px-4">
      <div className="max-w-6xl mx-auto text-center">
        <div className="animate-fade-in">

          {/* Name and Title */}
          <h1 className="text-3xl md:text-5xl font-bold text-secondary-800 mb-3 animate-slide-up">
            Rakesh Sharma Palla
          </h1>

          <h2 className="text-xl md:text-2xl font-semibold text-primary-700 mb-5 animate-slide-up">
            Senior Product & Program Management Leader
          </h2>

          <p className="text-lg md:text-xl text-secondary-600 font-medium mb-8 animate-slide-up">
            19+ Years Driving SaaS, Cloud, and Digital Transformation at Scale
          </p>

          {/* Tagline */}
          <p className="text-lg md:text-xl text-secondary-600 mb-8 max-w-3xl mx-auto leading-relaxed animate-slide-up">
            Executive leader with proven success driving product innovation, enterprise SaaS growth, and digital transformation for Fortune 500 clients
          </p>

          {/* Call-to-Action Button */}
          <div className="flex justify-center animate-slide-up">
            <button 
              onClick={handleLinkedInConnect}
              className="btn-secondary"
            >
              <svg className="w-5 h-5 inline mr-2" fill="currentColor" viewBox="0 0 24 24">
                <path d="M20.447 20.452h-3.554v-5.569c0-1.328-.027-3.037-1.852-3.037-1.853 0-2.136 1.445-2.136 2.939v5.667H9.351V9h3.414v1.561h.046c.477-.9 1.637-1.85 3.37-1.85 3.601 0 4.267 2.37 4.267 5.455v6.286zM5.337 7.433c-1.144 0-2.063-.926-2.063-2.065 0-1.138.92-2.063 2.063-2.063 1.14 0 2.064.925 2.064 2.063 0 1.139-.925 2.065-2.064 2.065zm1.782 13.019H3.555V9h3.564v11.452zM22.225 0H1.771C.792 0 0 .774 0 1.729v20.542C0 23.227.792 24 1.771 24h20.451C23.2 24 24 23.227 24 22.271V1.729C24 .774 23.2 0 22.222 0h.003z"/>
              </svg>
              Connect on LinkedIn
            </button>
          </div>

          {/* Key Stats */}
          <div className="mt-16 grid grid-cols-2 md:grid-cols-4 gap-8 animate-slide-up">
            <div className="text-center">
              <div className="text-3xl md:text-4xl font-bold text-primary-600 mb-2">$50M+</div>
              <div className="text-sm md:text-base text-secondary-600">SaaS Portfolio Growth</div>
            </div>
            <div className="text-center">
              <div className="text-3xl md:text-4xl font-bold text-primary-600 mb-2">100K+</div>
              <div className="text-sm md:text-base text-secondary-600">Enterprise Users</div>
            </div>
            <div className="text-center">
              <div className="text-3xl md:text-4xl font-bold text-primary-600 mb-2">20%</div>
              <div className="text-sm md:text-base text-secondary-600">Productivity Uplift</div>
            </div>
            <div className="text-center">
              <div className="text-3xl md:text-4xl font-bold text-primary-600 mb-2">15+</div>
              <div className="text-sm md:text-base text-secondary-600">Global Teams Led</div>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Hero;
