import React from 'react';

const Skills = () => {
  const skills = [
    {
      category: "People Leader",
      description: "President, IIBA Hyderabad Chapter; built professional development programs impacting 500+ members",
      icon: "üë•"
    },
    {
      category: "Recognition & Influence", 
      description: "Multiple Deloitte awards for thought leadership and driving client outcomes",
      icon: "üèÜ"
    },
    {
      category: "Change Agent",
      description: "Recognized for scaling Agile practices, building high-performing teams, and accelerating organizational transformation",
      icon: "üîÑ"
    },
    {
      category: "Community Builder",
      description: "Mentor, coach, and advocate for diversity, continuous learning, and people-first leadership",
      icon: "üåç"
    }
  ];

  return (
    <section id="skills" className="py-20 bg-white">
      <div className="max-w-6xl mx-auto px-4">
        <h2 className="section-title gradient-text">Executive Strengths</h2>
        
        <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-8">
          {skills.map((skill, index) => (
            <div key={index} className="card p-6 animate-slide-up animate-on-scroll" style={{ animationDelay: `${index * 0.1}s` }}>
              <div className="text-4xl mb-4">{skill.icon}</div>
              <h3 className="text-xl font-bold text-secondary-800 mb-3">{skill.category}</h3>
              <p className="text-secondary-600 leading-relaxed">{skill.description}</p>
            </div>
          ))}
        </div>

        {/* Additional Skills Section */}
        <div className="mt-16">
          <h3 className="text-2xl font-bold text-secondary-800 text-center mb-8">Technical & Methodological Expertise</h3>
          
          <div className="grid md:grid-cols-2 gap-8">
            <div className="card p-6">
              <h4 className="text-lg font-semibold text-secondary-800 mb-4">Product Management Tools</h4>
              <div className="flex flex-wrap gap-2">
                {['Jira', 'Confluence', 'Figma', 'Miro', 'ProductBoard', 'Aha!', 'Pendo', 'Mixpanel'].map((tool) => (
                  <span key={tool} className="px-3 py-1 bg-primary-100 text-primary-700 rounded-full text-sm font-medium">
                    {tool}
                  </span>
                ))}
              </div>
            </div>
            
            <div className="card p-6">
              <h4 className="text-lg font-semibold text-secondary-800 mb-4">Agile Methodologies</h4>
              <div className="flex flex-wrap gap-2">
                {['SAFe', 'Scrum', 'Kanban', 'Lean', 'Design Thinking', 'DevOps', 'CI/CD'].map((method) => (
                  <span key={method} className="px-3 py-1 bg-secondary-100 text-secondary-700 rounded-full text-sm font-medium">
                    {method}
                  </span>
                ))}
              </div>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Skills;
